import React from 'react';
 class App extends React.Component {
        constructor(props) {
          super(props);
          this.state = {
            fullname: "Mariama Ba",
            bio: "Becker Charles, Mbaye Saliou, Thioub Ibrahima (sous la direction de), AOF : Réalités et",
            imgSrc:"youtube_profile_image.png",
            profession:"Ecrivain",
            show:true
          };
        }

      render(){
              return(
              <div>
            <button  onClick={this.setState({show:!true})}> 
            Afficher/Masquer
            <>
            <h1> {this.state.fullname}</h1>
                  <h1> {this.state.fullname}</h1>
                  <p>{this.state.bio}</p>
                  <div>
                    <img src={this.state.imgSrc} alt="img-profile" />
                    <div>{this.state.profession}</div>
                  </div>
                </>
            </button>


            </div>
              )     
       }  
    }

export default App;
