class Person extends React.Component {
    state = {
        fullname: "adji",
        bio:"k,nf",
        imgSrc:"youtube_profile_image.png",
        profession:"dev",
        show:true

      };

      render(){
        return(
        <div>
          {

            (this.state.show) ?
            (
              <>
        <button  onClick={this.setState}> Afficher/Masquer</button>
            <h1>hello {this.state.fullname}</h1>
            <p>{this.state.bio}</p>
            <div>
              <img src={this.state.imgSrc} alt="img-profile" />
              <div>{this.state.profession}</div>
            </div>
              </>
            )
            :
                    <button  onClick={this.state.show}> Afficher/Masquer</button>

          }
        </div>
        )     
 }  
}
export default Person;
