import React from 'react';
 class App extends React.Component {
        constructor(props) {
          super(props);
          this.state = {
            fullname: "Mariama Ba",
            bio: "Becker Charles, Mbaye Saliou, Thioub Ibrahima (sous la direction de), AOF : Réalités et",
            imgSrc:"youtube_profile_image.png",
            profession:"Ecrivain",
          show:true
          }

        }
        showText = () => {
            //this.setState({ show: !true });
            return  this.show ? contain() : null
          }
          contain = ()=>{
          <>
            <p>{this.state.bio}</p>
            <div>{this.state.profession}</div>
            <div>
              <img src={this.state.imgSrc} alt="img-profile" />
            </div>
          </>
          }


      render(){
              return(
              <div>
            <button onClick={ this.showText=()=> {this.contain()}}><h1>Afficher </h1 ></button>
            </div>



              )     
       }  
    }

export default App;
