import React from 'react';

class Person extends React.Component {
  state = {
    fullname: "Sara",
    bio:"k,nf",
    imgSrc:"./youtube_profile_image.png",
    profession:"dev",

  };
  render() {
    return <> 
    <div>
      <button > ClickMe</button>

      <h1>hello {this.state.fullname}</h1>
      <p>{this.state.bio}</p>
      <div>
        <img src={this.state.imgSrc}  alt="img-profile"/>
        <span>{this.state.profession}</span>
      </div>
    </div>
    </>


  }
}
export default Person;
