import React from 'react';

class Person extends React.Component {
  state = {
    fullname: "Sara",
    bio:"k,nf",
    imgSrc:"youtube_profile_image.png",
    profession:"dev",
    Boolean

  };
  render() {
    return <> 
     <button onClick={this.state.Boolean } > ClickMe</button>

    <div>
     <h1>hello {this.state.fullname}</h1>
        <p>{this.state.bio}</p>
        <div>
          <img src={this.state.imgSrc} alt="img-profile" />
          <div>{this.state.profession}</div>
        </div>
    </div>
    </>


  }
}
export default Person;
