import React, { useState ,useEffect} from "react";
import Person from "./person";

const App = () => {
  const [ show , setShow] = useState()
  const [intervalId, setIntervalId] = useState(0);

  useEffect(() => {
    setInterval(() => {
      setShow(prevCount => prevCount + 1);
    }, 1000);
  }, []);
  return( 
    <>
      {
         show ? <Person /> : null
      }

    <button onClick={() => {setShow ? setShow(true):setShow(false) }} > Afficher </button>



    </>
  )
};

export default App;
